package java1206;

public class Inequality_sign_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 500;
		int b = 20;
		int c = 30;
		System.out.println(a<b);
		System.out.println(a>b);
		System.out.println(a+b<=c);
		System.out.println(a+b>=c);
	}

}
