package java1206;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;		// a에 10을 대입
		int b = 3; 		// b에 3을 대입
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
	}

}
