package java1209;

public class Array_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String[] a = {"Java", "Hello", "Programming"};
		
		for (String i : a) {
			System.out.println(i);
		}
		int [] b = {1, 2, 3, 4, 5};
		
		for (int i : b) {
			double j = (double)i/10;
			System.out.println(j);
		}
	}

}
