package java1214.exercise1214;

public class Object4_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 0;
		int j = 0;
		
		
		for (i=1;i<=6;i++) {
			for (j=1;j<=6;j++) {
				if (i + j == 6) {
					System.out.println("i: " + i + ", j: " + j);
				}
			}
		}
		
	}

}
